SqlConnection sqlConnection = new SqlConnection(@"Data Source=(LocalDB)\MSSQLLocalDB;AttachDbFilename=|DataDirectory|\Database2.mdf;");

SqlCommand sqlCommand = new SqlCommand("SELECT * FROM dbo.[Table] WHERE Age = 18", sqlConnection);

sqlConnection.Open();

SqlDataReader reader = sqlCommand.ExecuteReader(); // Executes the Query

while (reader.Read())
{
Console.WriteLine("Name is: " + reader.GetValue(1));
Console.WriteLine("Age is: " + reader.GetValue(2));
}

sqlConnection.Close();
